const PermissionManager = require('./permissions');
const RoleManager = require('./roles');

const permManager = new PermissionManager();
const roleManager = new RoleManager(permManager);

permManager.addPermission('SING');
roleManager.defineRole('SINGER', ['SING']);
roleManager.defineRole('ADMIN', ['READ', 'WRITE', 'DELETE']);

console.log('Does SINGER have SING permission?', roleManager.hasPermission('SINGER', 'SING')); // true
console.log('Does ADMIN have WRITE permission?', roleManager.hasPermission('ADMIN', 'WRITE')); // true
